DROP procedure IF EXISTS `SELECT_QUESTION_STATUSES`
#

CREATE PROCEDURE `SELECT_QUESTION_STATUSES` (
In request_id varchar(11)
)
BEGIN
SELECT * FROM question_statuses WHERE (request_id = '' OR id=request_id);
END
