END

DROP procedure IF EXISTS `INSERT_DRAFT_QUESTIONS`
#
CREATE PROCEDURE `INSERT_DRAFT_QUESTIONS` ( In request_id varchar(11), In request_status_id varchar(11), In request_category_id varchar(11), In request_minqual_id varchar(11), In request_maxqual_id varchar(11), In request_question varchar(200), In request_answer varchar(200), In request_creator_id varchar(11) ) BEGIN INSERT INTO questions (id, status_id, category_id, min_qual_id, max_qual_id, question, answer, creator_id) VALUES (IF(request_id='',null,request_id),request_status_id,request_category_id, request_minqual_id, request_maxqual_id, request_question, request_answer, request_creator_id) ON DUPLICATE KEY UPDATE category_id= IF(request_category_id = '', category_id, request_category_id), min_qual_id = IF(request_minqual_id = '', min_qual_id, request_minqual_id), max_qual_id = IF(request_maxqual_id = '', max_qual_id, request_maxqual_id), question = IF(request_question = '', question, request_question), answer = IF(request_answer = '', answer, request_answer), creator_id = IF(request_creator_id = '', creator_id, request_creator_id) ;
SELECT * FROM questions WHERE questions.id=request_id; END;;

DROP procedure IF EXISTS `REMOVE_QUESTION`;
#
CREATE PROCEDURE `REMOVE_QUESTION`(IN request_id varchar(11)) BEGIN DELETE FROM questions WHERE id=request_id; END
